mapper.insert:
insert into MEMBER (
MEMBER_ID, EMAIL, PASSWORD, PHONE, USER_NAME
)
values (
MEMBER_SQ.nextval, #{email}, #{password}, #{phone}, #{userName}
)

    select MEMBER_SQ.currval from dual

mapper.select:
select EMAIL, PASSWORD, PHONE, USER_NAME, ROLE, JOIN_DATE
from MEMBER
WHERE EMAIL = #{email}

mapper.update:
update MEMBER
set PASSWORD = #{password},
    PHONE = #{phone}
WHERE EMAIL = #{email}
